<script lang="tsx" setup>
import type { ApiReturnType, SetDataEvent } from '@vunk/core'
import type { PropType } from 'vue'
import { rCaptcha } from '@skzz/platform/api/login'
import { VkfInput } from '@vunk/form/components/input'
import { onMounted, ref } from 'vue'

defineProps({
  prop: {
    type: String as PropType<'captcha'>,
    default: 'captcha',
  },
  data: {
    type: Object,
    default: () => ({}),
  },
})
const emit = defineEmits({
  setData: (e: SetDataEvent) => e,
})

/* -------------------- */

const captcha = ref({
  captcha: 'data:image/png;base64,',
} as ApiReturnType<typeof rCaptcha>)

onMounted(r)

function r () {
  return rCaptcha().then((res) => {
    captcha.value = res
    emit('setData', {
      k: 'captchaId',
      v: res.captchaId,
    })
  })
}
function append () {
  return <img src={captcha.value.captcha} onClick={r} />
}
</script>

<template>
  <VkfInput
    v-bind="$attrs"
    class="captcha-input"
    :model-value="data[prop]"
    :input-slots="{
      append,
    }"
    placeholder="验证码"
    @update:model-value="$emit('setData', {
      k: 'captcha',
      v: $event,
    })"
  ></VkfInput>
</template>

<style lang="scss">
.captcha-input {
  .el-input-group__append{
    padding: 2px;
    img{
      height: 40px;
    }
  }
}
</style>
